import React, { useState } from "react";
import GeneralInfo from "./components/GeneralInfo";
import EducationInfo from "./components/EducationInfo";
import Resume from "./components/Resume";
import "./App.css";
import ExperienceInfo from "./components/ExperienceInfo";

const App = () => {
  // Initialize state

  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");

  const [schoolName, setSchoolName] = useState("");
  const [fieldOfStudy, setFieldOfStudy] = useState("");
  const [dateOfGraduation, setDateOfGraduation] = useState("");

  const [schools, setSchools] = useState("");

  const [workName, setWorkName] = useState("");
  const [position, setPosition] = useState("");
  const [tasks, setTasks] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const [works, setWorks] = useState("");

  // Handlers for general info
  const handleName = (newName) => {
    setFullName(newName);
  };

  const handleEmail = (newEmail) => {
    setEmail(newEmail);
  };

  const handlePhone = (newPhone) => {
    setPhone(newPhone);
  };

  // Handles for education info
  const handleSchoolName = (newSchoolName) => {
    setSchoolName(newSchoolName);
  };

  const handleFieldOfStudy = (newFieldOfStudy) => {
    setFieldOfStudy(newFieldOfStudy);
  };

  const handleDateOfGraduation = (newDateOfGraduation) => {
    setDateOfGraduation(newDateOfGraduation);
  };

  const handleSchoolSubmit = (e, school) => {
    setSchools(schools.concat(school));
    handleSchoolName(e);
    handleFieldOfStudy(e);
    handleDateOfGraduation(e);
  };

  return (
    <div className="App">
      <div className="cv-input">
        <h1>CV Input</h1>
        <GeneralInfo
          fullName={fullName}
          email={email}
          phone={phone}
          handleName={handleName}
          handleEmail={handleEmail}
          handlePhone={handlePhone}
        />
        <EducationInfo
          schoolName={schoolName}
          fieldOfStudy={fieldOfStudy}
          dateOfGraduation={dateOfGraduation}
          schools={schools}
          handleSchoolSubmit={handleSchoolSubmit}
        />
        <ExperienceInfo
          workName={workName}
          position={position}
          tasks={tasks}
          startDate={startDate}
          endDate={endDate}
        />
      </div>
      <div className="cv-output">
        <h1>CV Output</h1>
        <Resume />
      </div>
    </div>
  );
};

export default App;
