import React from "react";

const Resume = ({
  fullName,
  email,
  phone,
  schools,
  works,
  handleName,
  handleEmail,
  handlePhone,
}) => {
  return (
    <div>
      <h1>{fullName}</h1>
      <h3>{email}</h3>
      <h3>{phone}</h3>
      <hr />
      <ul>
        {schools?.map((index) => (
          <li key={index.schoolName} value={index.schoolName}>
            {index.schoolName} - {index.fieldOfStudy} - {index.dateOfGraduation}
            <div>
              <p onClick={() => deleteSchool(index.schoolName)}>(delete)</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Resume;
