import React from "react";

const GeneralInfo = ({
  fullName,
  email,
  phone,
  handleName,
  handleEmail,
  handlePhone,
}) => {
  const handleNameChange = (e) => {
    handleName(e.target.value);
  };

  return (
    <div>
      <form>
        <label>Name: </label>
        <input onChange={handleNameChange}></input>
        <br />
        <br />
        <label>Email: </label>
        <input type={email}></input>
        <br />
        <br />
        <label>Phone: </label>
        <input type={phone}></input>
        <br />
        <br />
      </form>
      <h3>{fullName}</h3>
      <h3>{email}</h3>
      <h3>{phone}</h3>
    </div>
  );
};

export default GeneralInfo;
